import logo from './logo.svg';
import './App.css';
import { useState} from "react";
import songData from "./assets/all-songs.json";
import SongList from './components/song-list.js';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Accordion from 'react-bootstrap/Accordion';
import Form from 'react-bootstrap/Form';

songData.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});

function App() {
  const [cart, setCart] = useState({});
  const [length, setLength] = useState([3,10]);
  const [filterLength, setFilterLength] = useState(false);
  const [filterArtist, setFilterArtist] = useState({
    'TXT':true,
    'Seventeen':true,
    'Enhypen':true,
    'aespa':true,
  }); //change based on artists
  const [sort, setSort] = useState(false);
  const [playlistMinutes, setPlaylistMinutes] = useState(0);
  const [playlistSeconds, setPlaylistSeconds] = useState(0);
  function changeArtistFilter(artist){
    const filterArtistCopy = {...filterArtist};
    filterArtistCopy[artist] = !filterArtistCopy[artist];
    setFilterArtist(filterArtistCopy);
  }

  return (
    <div className="App">
      <header>
        {/* <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a> */}
      </header>
      <body>
      {
        <Accordion>
          <Accordion.Item eventKey="0">
            <Accordion.Header>Filter</Accordion.Header>
            <Accordion.Body>
              <Accordion>
                <Accordion.Item eventKey="0">
                  <Accordion.Header>Filter by Artist</Accordion.Header>
                  <Accordion.Body>
                    <Form.Check
                      type='checkbox'
                      id='txt'
                      defaultChecked = 'true'
                      label='TXT'
                      onClick={() => changeArtistFilter('TXT')}
                    />
                  </Accordion.Body>
                </Accordion.Item>
                <Accordion.Item eventKey="1">
                  <Accordion.Header>Filter by Price</Accordion.Header>
                  <Accordion.Body>
                    <Form.Check 
                      type="checkbox"
                      id="custom-switch"
                      label="Filter on"
                    />
                    <>
                    <Form.Label>Range</Form.Label>
                    <Form.Range />
                    </>
                  </Accordion.Body>
                </Accordion.Item>
              </Accordion>
            </Accordion.Body>
          </Accordion.Item>
          <Accordion.Item eventKey="1">
            <Accordion.Header>Sort</Accordion.Header>
            <Accordion.Body>
              <Form.Check
                type='checkbox'
                id='default-checkbox'
                label='Sort by time duration (ascending)'
              />
              <Form.Check
                type='checkbox'
                id='default-checkbox'
                label='Sort by time duration (descending)'
              />
            </Accordion.Body>
          </Accordion.Item>
        </Accordion>
      }
      {
      <SongList lengthFilter = {filterLength} length = {length} artists = {filterArtist} sort = {sort} />
      }
      </body>
    </div>
  );
}

export default App;
