        </a> */}
      </header>
      <body>
      {
        <DropdownButton id="filter" title="Filter Options">
          <DropdownButton id="dropdown-basic-button" title="Dropdown button">
            <Form.Check
              type='checkbox'
              id='default-checkbox'
              label='hi'
            />
          <>
            <Form.Label>Range</Form.Label>
            <Form.Range />
          </>
          </DropdownButton>
          <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
          <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
        </DropdownButton>
      }
      {
      <SongList lengthFilter = {filterLength} length = {length} artists = {filterArtist} sort = {sort} />
      }
      </body>
    </div>
  );
}

export default App;
